import React, { useState, useEffect } from "react";
import axios from "axios";
import apiKeys from "./apiKeys";
import ReactAnimatedWeather from "react-animated-weather";
import NodeGeocoder from "node-geocoder";

function Forcast(props) {
  const [query, setQuery] = useState("");

  const options = {
    provider: "google",

    // Optional depending on the providers
    fetch: customFetchImplementation,
    apiKey: "YOUR_API_KEY", // for Mapquest, OpenCage, APlace, Google Premier
    formatter: null, // 'gpx', 'string', ...
  };
  const geocoder = NodeGeocoder(options);

  const search = () => {
    console.log(query);
    // await geocoder.geocode(query);
    // try {
    //   const response = await axios.get(
    //     `${apiKeys.base}current?lat=${props.lat}&lon=${props.lon}&key=${apiKeys.key}`
    //   );
    //   const result = response.data;
    //   setWeather(result);
    //   setQuery("");
    //   console.log(result);
    // } catch (error) {
    //   console.log(error);

    //   setQuery("");
    //   setError({ message: "Not Found", query: query });
    // }
  };

  const defaults = {
    color: "white",
    size: 112,
    animate: true,
  };

  useEffect(() => {
    search("Feltham");
  }, []);

  return (
    <div className="forecast">
      <div className="forecast-icon">
        <ReactAnimatedWeather
          icon={props.icon}
          color={defaults.color}
          size={defaults.size}
          animate={defaults.animate}
        />
      </div>
      <div className="today-weather">
        <h3>{props.weather.city}</h3>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search any city"
            onChange={(e) => setQuery(e.target.value)}
            value={query}
          />
          <div className="img-box">
            <img
              src="https://images.avishkaar.cc/workflow/newhp/search-white.png"
              onClick={search}
              alt="search icon"
            />
          </div>
        </div>
        <ul>
          {props.weather.description !== "undefined" ? (
            <div>
              <li className="cityHead">
                <p>
                  {props.weather.city}, {props.weather.country}
                </p>
              </li>
              <li>
                Temperature
                <span className="temp">
                  {Math.round(props.weather.temperatureC)}Â°c (
                  {props.weather.description})
                </span>
              </li>
              <li>
                Humidity
                <span className="temp">
                  {Math.round(props.weather.humidity)}%
                </span>
              </li>
              <li>
                Visibility
                <span className="temp">
                  {Math.round(props.weather.visibility)} mi
                </span>
              </li>
              <li>
                Wind Speed
                <span className="temp">
                  {Math.round(props.weather.wind.speed)} Km/h
                </span>
              </li>
            </div>
          ) : (
            <li>
              {error.query} {error.message}
            </li>
          )}
        </ul>
      </div>
    </div>
  );
}
export default Forcast;
